package t1032;import java.util.Scanner;public class Main {	public static void main(String[] args) {		Scanner scanner = new Scanner(System.in);		int m = scanner.nextInt();		int n = scanner.nextInt();		System.out.println(a(m,n));	}		static int a(int m,int n){		if(m == 0 )			return n+1;		else if(n ==0)			return a(m-1, 1);		else {			return a(m-1, a(m, n-1));		}	}}